import React from 'react';
import Button from 'react-bootstrap/Button';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const Skill = ({ text, level, skills, skill, setSkills, percent }) => {
    // These handlers need to be be find&update requests to mongodb
    // const levelUpHandler = () => {

    //     setSkills(skills.map((item) => {
    //         if (item.id === skill.id) {
    //             if (skill.percent == 90) {
    //                 return { ...item, percent:0, level:level+1}
    //             } else {
    //                 return {
    //                     ...item, percent:percent+10
    //                 }
    //             }

    //         }
    //         return item
    //     }))
    // }
    const levelUpHandler = async (e) => {
        let level;
        let percent; 
        if (e.percent == 90) {
            percent = 0;
            level = e.level += 1;
        } else {
            percent = e.percent + 10;
            level = e.level; 
        }
        let data = {_id:e._id, level:level, percent:percent}
        console.log("This be the data: ", data)
        const request = await axios.put("http://localhost:3001/api/users/update-skill", data)
        .then((err, res) => {
            if (err) {console.log(err)}
            else{ console.log(res)}
        })

    }

    const levelDownHandler = () => {
        setSkills(skills.map((item) => {
            if (item.id === skill.id) {
                if (skill.percent == 0 && skill.level > 1) {
                    return { ...item, percent:90, level:level-1}
                } else if (skill.percent == 0 && skill.level == 1) {
                    return item
                } else {
                        return {
                            ...item, percent:percent-10
                        }

                }

            }
            return item
        }))
    }
    // Add a find and delete handlers for when a user wants to delete a skill

    return (
        <div className='skill'>
            <Button onClick={levelDownHandler} className='bar-btn'>
                    <i className='fas fa-minus'></i>
            </Button>
            <div>

                <div className='saved-skill'>
                    { text }  
                </div>

                <div className="progressBar" style={{ width:500}}>
                    <ProgressBar now={percent} label={`${percent}%`}/>
                </div>

            </div>
            <div className='level-num'>
                { level }
            </div>
            <Button onClick={levelUpHandler} className='bar-btn'>
                    <i className='fas fa-plus'></i>
            </Button>

        </div>
    );
}


export default Skill;
