import React from 'react';
import Button from 'react-bootstrap/Button';

function Form({inputText, setInputText, skills, setSkills}) {
    const inputTextHandler = () => {
        setInputText(inputText)
    }

    const submitHandler = (e) => {
        e.preventDefault();
        setSkills([
            ...skills, {text: inputText, level:1, percent:10, id:Math.random() * 1000}
        ]);
        // Reset inputText
        setInputText("");
    }

    return(
        <div className='input-form'>
            <div>
                <input 
                    onChange={inputTextHandler}
                    type='text'
                    className='input-field'

                />
                <Button onClick={submitHandler} variant="primary" type='submit'><i class="fas fa-plus"></i></Button>{' '}

            </div>
        </div>
    )
}


export default Form;
