import React, { useState }from "react";
import { withStyles } from "@material-ui/core/styles";
import { login } from "./LoginStyles";
import { Link } from 'react-router-dom';
import axios from "axios";

import InputAdornment from "@material-ui/core/InputAdornment";
import CssBaseline from "@material-ui/core/CssBaseline";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import { FormControl, Input, InputLabel, Button } from "@material-ui/core";
import PeopleAltIcon from "@material-ui/icons/PeopleAlt";
import VisibilityTwoToneIcon from "@material-ui/icons/VisibilityTwoTone";
import VisibilityOffTwoToneIcon from "@material-ui/icons/VisibilityOffTwoTone";




// Can turn this into a function to pass setSession down to it
function Login (props) {
    const [loginInfo, setLoginInfo] = useState({
        email: "",
        password: "",
        hidePassword: true,
        error: null,
        errorOpen: false
    })

    const errorClose = e => {
    setLoginInfo({
        errorOpen: false
    });
    };

    const handleChange = name => e => {
    setLoginInfo({
        [name]: e.target.value
    });
    };

    const showPassword = () => {
        setLoginInfo(prevState => ({ hidePassword: !prevState.hidePassword }));
      };

    const isValid = () => {
        if (loginInfo.email === "") {
            return false;
        } else if (loginInfo.password === "") {
            return false;
        }
        return true;
        };
    const loginHandler = e => {
        e.preventDefault();
        const loginInfo = {
            email: loginInfo.email,
            password: loginInfo.password
        }
        axios.post("http://localhost:3001/api/users/login", loginInfo)
            .then((res) => {
                console.log(res.data);

            }).catch((error) => {
                console.log(error)
            })
    }


    const theme = login();
    console.log("These are the themes: ", theme)
    return (
        <div className={theme.main}>
            <CssBaseline /> 

            <Paper className={theme.paper}>
                <Avatar className={theme.avatar}>
                    <PeopleAltIcon className={theme.icon} />
                </Avatar>

                <form
                    className={theme.form}
                    onSubmit={() => this.loginHandler}
                    // add on submit handler
                >
                    <FormControl required fullWidth margin="normal">
                        <InputLabel htmlFor="email" className={theme.labels}>
                            e-mail
                        </InputLabel>
                        <Input
                            name="email"
                            type="email"
                            autoComplete="email"
                            className={theme.inputs}
                            disableUnderline={true}
                            onChange={handleChange("email")}
                        />
                    </FormControl>
                    <FormControl required fullWidth margin="normal">
                        <InputLabel htmlFor="password" className={theme.labels}>
                            password
                        </InputLabel>
                        <Input
                            name="password"
                            autoComplete="password"
                            className={theme.inputs}
                            disableUnderline={true}
                            onChange={handleChange("password")}
                            type={loginInfo.hidePassword ? "password" : "input"}
                            endAdornment={
                            loginInfo.hidePassword ? (
                                <InputAdornment position="end">
                                <VisibilityOffTwoToneIcon
                                    fontSize="default"
                                    className={theme.passwordEye}
                                    onClick={showPassword}
                                />
                                </InputAdornment>
                            ) : (
                                <InputAdornment position="end">
                                <VisibilityTwoToneIcon
                                    fontSize="default"
                                    className={theme.passwordEye}
                                    onClick={showPassword}
                                />
                                </InputAdornment>
                            )
                            }
                        />
                    </FormControl>

                    <Button
                        disabled={!isValid()}
                        disableRipple
                        fullWidth
                        variant="outlined"
                        className={theme.button}
                        type="submit"
                        onClick={loginHandler}
                        >
                        Log In
                    </Button>
                    <Link to='/register'>
                        Don't have an account yet?
                    </Link>
                </form>
            </Paper>
        </div>
      );


}


export default withStyles(login)(Login);
