import React from 'react';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Skill = ({ text, level, skills, skill, setSkills, percent }) => {

    const levelUpHandler = () => {
        console.log(skills)
        setSkills(skills.map((item) => {
            if (item.id === skill.id) {
                return {
                    ...skill, percent:percent+10
                }
            }
            return skill
        }))
    }

    return (
        <div className='skill'>
            <button className='minus-btn'>
                    <i className='fas fa-minus'></i>
            </button>
            <div>

                <div className='saved-skill'>
                    { text }

                </div>
                <div className="progressBar" style={{ width:400}}>
                    <ProgressBar now={percent} label={`${percent}%`}/>
                    { level }
                </div>

            </div>
            <button onClick={levelUpHandler} className='plus-btn'>
                    <i className='fas fa-plus'></i>
            </button>

        </div>
    );
}


export default Skill;
