import React, { useEffect, useState } from'react';
import './App.css';
import Form from './Components/Form';
import Navbar from './Components/Navbar';
import SkillsList from './Components/SkillsList';
import Logins from './Components/Logins';
import Registration from './Components/Registration';


import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import axios from 'axios';






function App() {
  // States
  const [inputText, setInputText] = useState("");
  const [skills, setSkills] = useState([]);
  const [session, setSession] = useState({auth: false, token:"", user_id:""})

  const { isLoggedIn, token, user_id } = session
  // If the user is logged in we need to populate skills state from mongodb. 


  // Refer to react docs for conditional learning

  // call-back function for Logins component
  const  userAuth = (data) => {
    setSession({
      auth:true,
      token: data.token,
      user_id: data.user_id
    })
    console.log('User logged in successfully!')

  }
  const logoutHandler = () => {
    if (isLoggedIn) {
      setSession({auth: false, token:"", user_id:""})
    }
    return <Redirect to="http://localhost:3000/login" />
  }

  useEffect( () => {
    const fetchSkills = async () => {
      await axios.get("http://localhost:3001/api/users/fetchSkills")
      .then(res => {
        setSkills(res.data)
      });
    };
    fetchSkills()

  }, []) 



  return (
    <Router>
      <div className="App">
        <Navbar logoutHandler={logoutHandler} />

        <Switch>

          <Route path='/' exact>
          <div className='app-container'>
          <h1 className='app-name'>Level Up App</h1>
            <Form 
              inputText={inputText} 
              setInputText={setInputText} 
              skills={skills} 
              setSkills={setSkills}
              session={session} 
            />
            <SkillsList 
              skills={skills} 
              setSkills={setSkills} 
            />
          </div>
          </Route>
          <Route path='/register' component={Registration} />
          <Route path='/login'>
            <Logins 
              userAuth={userAuth} 
            />
          </Route>


        </Switch>

      </div>
    </Router>

  );
}

export default App;
