import React from 'react';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Skill = ({ key, text, level, skills, setSkills, percent }) => {

    const plusButtonHandler = (e) => {
        if (e.key == key) {
            setSkills({...skills, percent:percent+10})
            percent += 10
            console.log(percent)
        } else {
            // Add a way to target the level here
            percent = 0
        }
    }

    return (
        <div className='skill'>
            <button className='minus-btn'>
                    <i className='fas fa-minus'></i>
            </button>
            <div>

                <div className='saved-skill'>
                    { text }

                </div>
                <div className="progressBar" style={{ width:400}}>
                    <ProgressBar now={percent} label={`${percent}%`}/>
                    { level }
                </div>

            </div>
            <button onClick={plusButtonHandler} className='plus-btn'>
                    <i className='fas fa-plus'></i>
            </button>

        </div>
    );
}


export default Skill;
