  return (
    <div className="skill">
      <Button onClick={levelDownHandler} className="bar-btn">
        <i className="fas fa-minus"></i>
      </Button>
      <div>
        <div className="saved-skill">{text}</div>

        <div className="progressBar" style={{ width: 500 }}>
          <ProgressBar now={percent} label={`${percent}%`} />
        </div>
      </div>
      <div className="level-num">{level}</div>
      <Button onClick={levelUpHandler} className="bar-btn">
        <i className="fas fa-plus"></i>
      </Button>
    </div>
  );
};

export default Skill;
