import React, { useState } from'react';
import './App.css';
import Form from './Components/Form';
import Navbar from './Components/Navbar';
import SkillsList from './Components/SkillsList';
import Logins from './Components/Logins';
import Registration from './Components/Registration';


import { BrowserRouter as Router, Switch, Route, useHistory} from 'react-router-dom';






function App() {
  // States
  const [inputText, setInputText] = useState("");
  const [skills, setSkills] = useState([]);
  const [session, setSession] = useState({auth: false, token:""})

  const { isLoggedIn } = session
  const { token } = session

  const history = useHistory()
  // Refer to react docs for conditional learning

  // call-back function for Logins component
  const  userAuth = (data) => {
    setSession({
      auth:true,
      token: data.token
    })
    console.log('User logged in successfully!')

  }


  return (
    <Router>
      <div className="App">
        <Navbar />

        <Switch>

          <Route path='/' exact>
          <div className='app-container'>
          <h1 className='app-name'>Level Up App</h1>
            <Form inputText={inputText} setInputText={setInputText} skills={skills} setSkills={setSkills} />
            <SkillsList skills={skills} setSkills={setSkills} />
          </div>
          </Route>
          <Route path='/register' component={Registration} />
          <Route path='/login'>
            <Logins userAuth={userAuth} />
          </Route>


        </Switch>

      </div>
    </Router>

  );
}

export default App;
