import React, { useEffect, useState } from'react';
import './App.css';
import Form from './Components/Form';
import Navbar from './Components/Navbar';
import SkillsList from './Components/SkillsList';
import Logins from './Components/Logins';
import Registration from './Components/Registration';
import Greeting from './Components/Greeting';


import { BrowserRouter as Router, Switch, Route, useHistory, Redirect } from 'react-router-dom';
import axios from 'axios';






function App() {
  // Get Token from local storage
  // const initialToken = localStorage.getItem('token');
  const initialUser = localStorage.getItem('user_id');
  // States
  const [inputText, setInputText] = useState("");
  const [skills, setSkills] = useState([]);
  const [session, setSession] = useState({auth: false, token:"", user_id:initialUser, refresh: false})

  var history = useHistory();
  // Considering using object deconstruction
  var refresh  = session.refresh;
  var isLoggedIn = session.user_id ? true: false
  // var token = session.token;
  // If the user is logged in we need to populate skills state from mongodb. 


  // Refer to react docs for conditional learning

  // call-back function for Logins component
  const  userAuth = (data) => {
    setSession({
      auth:true,
      token: data.token,
      user_id: data.user_id
    })
    console.log('User logged in successfully!')

  }



  const logoutHandler = () => {
    if (isLoggedIn) {
      setSession({auth: false, token:"", user_id:""});
      localStorage.removeItem("user_id");
      return <Route><Redirect to='/login'/></Route>
    }

  }


  const refreshHandler = () => {
    setSession({...session, refresh: !session.refresh})
  }

  useEffect(() => {
    console.log("USE EFFECT MOUNTED!!")
    const fetchSkills = async () => {
      const response = await axios.get('http://localhost:3001/api/users/fetchSkills');
      console.log("response: ", response.data)
      setSkills(response.data.filter((skill) => {
          if (skill.user_id == session.user_id) {
            return skill
          }
      }))
    }
    fetchSkills();



  },[refresh] );




  return (
    <Router>
      <div className="App">
        <Navbar logoutHandler={logoutHandler} />

        <Switch>

        {isLoggedIn && <Route path='/' exact>
          <div className='app-container'>
          <h1 className='app-name'>Level Up App</h1>

            <Form 
              inputText={inputText} 
              setInputText={setInputText} 
              skills={skills} 
              setSkills={setSkills}
              session={session}
              setSession={setSession} 
              refreshHandler={refreshHandler}
            />,
            <SkillsList 
              isLoggedIn={isLoggedIn} 
              skills={skills} 
              setSkills={setSkills}
              session={session}
              setSession={setSession} 
              refreshHandler={refreshHandler}
            />
          </div>
          </Route>}
          {!isLoggedIn &&<Route path='/' exact>
            <Greeting />
          </Route> }
          <Route path='/register' component={Registration} />
          <Route path='/login'>
            <Logins 
              userAuth={userAuth} 
            />
          </Route>
          <Route path='*'>
            <Redirect to ='/' />
          </Route>


        </Switch>

      </div>
    </Router>

  );
}

export default App;
