import React from 'react';
import Button from 'react-bootstrap/Button';
import { ProgressBar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const Skill = ({ refreshHandler, text, level, skills, skill, setSkills, percent, setSession, session }) => {
    // These handlers need to be be find&update requests to mongodb
    // const levelUpHandler = () => {

    //     setSkills(skills.map((item) => {
    //         if (item.id === skill.id) {
    //             if (skill.percent == 90) {
    //                 return { ...item, percent:0, level:level+1}
    //             } else {
    //                 return {
    //                     ...item, percent:percent+10
    //                 }
    //             }

    //         }
    //         return item
    //     }))
    // }
