    //     let data = {_id:skill._id, level:skill.level, percent:skill.percent}; 
    //     if (skill.percent == 90) {
    //         data.percent = 0
    //         data.level += 1
    //     } else {
    //         data.percent += 10
    //     }
    //     axios.put("http://localhost:3001/api/users/update-skill", data)
    //     .then((err, res) => {
    //         if (err) {console.log(err)}
    //         else{ 
    //             console.log(res)
    //             refreshHandler();

    //         }
    //     })



    // }
    const levelUpHandler =  () => {
        let data = {_id:skill._id, level:skill.level, percent:skill.percent}; 
        if (skill.percent == 90) {
            data.percent = 0
            data.level += 1
        } else {
            data.percent += 10
        }
        useEffect(() => {
            console.log("SKILL EFFECT MOUNTED!!")
            const fetchSkills = async () => {
              const response = await axios.put('http://localhost:3001/api/users/update-skill', data);
              console.log("response: ", response.data)
              setSkills(response.data.filter((skill) => {
                  if (skill.user_id == session.user_id) {
                    return skill
                  }
              }))
            }
            fetchSkills();

          },  []);



    }







    const levelDownHandler = () => {
        setSkills(skills.map((item) => {
            if (item.id === skill.id) {
                if (skill.percent == 0 && skill.level > 1) {
                    return { ...item, percent:90, level:level-1}
                } else if (skill.percent == 0 && skill.level == 1) {
                    return item
                } else {
                        return {
                            ...item, percent:percent-10
                        }

                }

            }
            return item
        }))
    }
    // Add a find and delete handlers for when a user wants to delete a skill

    return (
        <div className='skill'>
            <Button onClick={levelDownHandler} className='bar-btn'>
                    <i className='fas fa-minus'></i>
            </Button>
            <div>

                <div className='saved-skill'>
                    { text }  
                </div>

                <div className="progressBar" style={{ width:500}}>
                    <ProgressBar now={percent} label={`${percent}%`}/>
                </div>

            </div>
            <div className='level-num'>
                { level }
            </div>
            <Button onClick={levelUpHandler} className='bar-btn'>
                    <i className='fas fa-plus'></i>
            </Button>

        </div>
    );
}


export default Skill;
