import React, { useState } from'react';
import './App.css';
import Form from './Components/Form';
import Navbar from './Components/Navbar';
import SkillsList from './Components/SkillsList';
import axios from 'axios';

import { Route, BrowserRouter } from "react-router-dom";

import Registration from "./components/Registration";

const routes = (
  <BrowserRouter>
    <Route exact path="/" component={Registration} />
  </BrowserRouter>
);

function App() {
  // State stuff
  const [inputText, setInputText] = useState("");
  const [skills, setSkills] = useState([]);
  const [logins, setLogins] = useState({});

  // Functions



  function updateLogins(e) {
    setLogins({...logins, [e.target.name]:e.target.value})
  }

  const submitHandler = (e) => {
    e.preventDefault();

    const userObject = {
      userName : logins.username,
      userPassword: logins.password
    };
    axios.post('http://localhost:3001/add-user', userObject)
      .then((res) => {
        console.log(res.data)
      }).catch((error) => {
        console.log(error)
      });
      setLogins({});
  }

  const loadUsers = () => {
    const response = axios.get('http://localhost:3001/all-users', )
    .then((response) => {console.log(response)})
  } 



  return (
    <div className="App">
      {/* <nav>
          <input onChange={updateLogins} name="username" placeholder="User Name">
          </input>
          <input onChange={updateLogins} name="password" placeholder="Password">
          </input>
          <button onClick={submitHandler}>Login</button>
          <button onClick={loadUsers}>Load Users</button>
        </nav> */}
        <Navbar 
          routes={routes}
        />
      <div className='app-container'>
        <h1 className='app-name'>Level Up App</h1>
        <Form 
          inputText={inputText}
          setInputText={setInputText}
          skills={skills}
          setSkills={setSkills}
        />

        <SkillsList
          skills={skills}
          setSkills={setSkills}
        />
      </div>

    </div>
  );
}

export default App;
