import React from 'react';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

function Form( { addSkillCallback, inputText, setInputText, skills, setSkills, session, setSession }) {
    const inputTextHandler = (e) => {
        setInputText(e.target.value)

    }
    // Removed the math.random() for id since the object will have its own id in mongo_db. 
    // Added id for user specific skills
    const submitHandler = (e) => {
        e.preventDefault();
        const newSkill = {text: inputText, level:1, percent:0, user_id:session.user_id};
        if (inputText != "") {
            // setSkills([...skills, newSkill]);
            axios.post("http://localhost:3001/api/users/add-skill", newSkill)
            .then((res) => {
                console.log("POST from forms",res.data)
                addSkillCallback();
            })
        }
        // Try making a axios post request: Request sucessful - Moved axios request into if statement

        // Reset inputText
        setInputText("");



    }

    return(
        <div className='input-form'>
            <div>
                <input 
                    onChange={inputTextHandler}
                    type='text'
                    className='input-field'

                />
                <Button onClick={submitHandler} variant="primary" type='submit'><i className="fas fa-plus"></i></Button>{' '}

            </div>
        </div>
    )
}


export default Form;
